import Card from './component/Card';
import './App.css';

function App() {
  let prd=[{ "id": 101, "name": "Basket Ball", "image": "basketball.png", "price": 150 }, { "id": 102, "name": "Football", "image": "football.png", "price": 120 }, { "id": 103, "name": "Soccer", "image": "soccer.png", "price": 110 }, { "id": 104, "name": "Table Tennis", "image": "table-tennis.png", "price": 130 }, { "id": 105, "name": "Tennis", "image": "tennis.png", "price": 100 }];
  return (
    <div className="App" id="box">
    <Card head="1. Java " title="Dr. James Arthur Gosling" para="Java was developed and supported earlier by Sun Microsystem and now by Oracle, after their acquisition of Sun Microsystem in January 2010." />

    <Card head="2. C " title="Dennis MacAlistair Ritchie" para="An American computer scientist, created the C programming language between 1967 and 1973 at AT& T Bell Labs." />

    <Card head="3. C++" title="Bjarne Stroustrup" para="C++, as the name suggested, is the next-generation language at the time C was popular. It comes with an object-oriented programming feature."/>

    <Card head="4. Python" title="Guido van Rossum" para="Python is a general-purpose, high-level programming language whose design philosophy emphasizes code readability. Its syntax is said to be bright and expressive. Python is designed by Guido van Rossum of CWI."/>

    <Card head="" title="" para=""/>
    </div>
  );
}

export default App;
